import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { apiResponse: "" };
  }

  callAPI() {
    fetch("http://localhost:9000/api/v1/footballers")
        .then(res => res.text())
        .then(res => this.setState({ apiResponse: res }));
  }

  componentWillMount() {
    this.callAPI();
  }

  render() {
    return (
      <div className="App">
        <h1 className="App-title">Search for Greatest Footballers!!!</h1>
        <input type="text" onKeyPress={getFootballPlayers} />
        <p className="App-into">{this.state.apiResponse}</p>
      </div>
    );
  }
}

export default App;
